package practise;

public class forloop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          for(int i=0; i<5; i++)
          {
        	  System.out.println("Forward Loop"+" "+i);
       
          }
          for(int i=5; i>=0; i-- )
          {
        	  System.out.println("backward Loop"+" "+i);
        	  
          } 
          for(int i=7; i>=3; i--)
          {
        	  System.out.println("backward loop"+""+i);
	}
          
         for(int i=1; i<=7; i=i+2)
         {
        	 System.out.println("Forward Loop"+" "+i);
         }
}
}